// Load dependencies.
var moment = require('moment')

/**
 * Simplified syntax for getting the current time as a Unix timestamp
 * in milliseconds.
 * 
 */
exports.now = function() {
    return new Date().getTime()
}

/**
 * Simplified syntax for parsing a time string regardless of whether it's
 * specified as an integer Unix timestamp or a MM-DD-YYYY string.
 * 
 */
exports.parse_time = function(time) {
    var timestamp = moment(time).valueOf()
    if (!timestamp) {
        timestamp = moment(parseInt(time)).valueOf()
    }

    return timestamp
}
